import java.util.Objects;/* * Click nbfs://nbhost/SystemFileSystem/Templates/Licenses/license-default.txt to change this license * Click nbfs://nbhost/SystemFileSystem/Templates/Classes/Class.java to edit this template *//** * * @author JYrik */public class Personagem  {    String nome;    int vida;    int energia;    int id;                public Personagem(String nome , int id ) {        this.nome = nome;        this.vida = 3;        this.energia = 3;        this.id = id;    }    public String getNome() {        return nome;    }    public void setNome(String nome) {        this.nome = nome;    }    public int getVida() {        return vida;    }    public void setVida(int vida) {        this.vida = vida;    }    public int getEnergia() {        return energia;    }    public void setEnergia(int energia) {        this.energia = energia;    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    @Override    public int hashCode() {        int hash = 7;        hash = 53 * hash + Objects.hashCode(this.nome);        hash = 53 * hash + this.vida;        hash = 53 * hash + this.energia;        return hash;    }    @Override    public boolean equals(Object obj) {        if (this == obj) {            return true;        }        if (obj == null) {            return false;        }        if (getClass() != obj.getClass()) {            return false;        }        final Personagem other = (Personagem) obj;        if (this.vida != other.vida) {            return false;        }        if (this.energia != other.energia) {            return false;        }        return Objects.equals(this.nome, other.nome);    }        }